# Creating a Database for Bimtan Royals Enterprise (Ojo Branch)
create database BimtanRoyal;

# Using database BimtanRoyal
Use bimtanRoyal;

# Creating a Sales transaction table that store the company sales activities
create table `Sales`(
transaction_id varchar (10) primary Key not null,
transaction_date date not null,
customer_id int not null, foreign key (customer_id) references customers (Customer_id),
product_id Varchar (10) not null, foreign key (product_id) references products (product_id),
Rep_id int not null, foreign key (Rep_id) references representatives (Rep_id),
quantity_Sold int not null,
total_Amount int not null);

 #Creating a Customer Table that make purchases from the company
 create table `customers` (
 customer_id int primary key not null,
 Customer_name varchar(45) not null,
 email varchar(45) not null,
 address varchar(45)not null);
 
 # Creating a Product table that enlist the type of services the company renders
 create table `products` (
 product_id varchar (10) primary key not null,
 product_name varchar(45) not null,
 category varchar(45) not null,
 Price int not null);
 
 # Creating Sales Representative table of the company
 Create table `Representatives` (
 Rep_id int primary key not null,
 Rep_name varchar(45) not null,
 Contact varchar(45) not null);
 
